import React from "react"

class Comp extends React.Component{
    constructor(props){
    super(props);
    this.state ={
        mouseState:false
        };  

    this.mouseHandleDown = this.mouseHandleDown.bind(this);
    this.mouseHandleUp = this.mouseHandleUp.bind(this);
    }

    mouseHandleDown() {
        console.log("mouse down");
        this.setState({mouseState:true});

    }
    mouseHandleUp()
    {
        console.log("mouse up");
        this.setState({mouseState:false});
    }
    img()
    {
        if(this.state.mouseState === true)
        {
            return (
                <img alt="img" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXlZ_mabusE4wKmnQR_Z0x5wjqkC9clX-aZg&usqp=CAU"></img>
            )
            
        }
        else{
            return(
                <img alt="img" src="https://miro.medium.com/max/1490/1*F9mmka7ZZahZ_vysv1KA2g.png" ></img>
            )
        }
    }
    render()
    {
    //    console.log(this.state.mouseState);
        return(
            <div onMouseEnter={this.mouseHandleDown} 
            onMouseLeave={this.mouseHandleUp}>
                {this.img}
            </div>
        )
    }
}

export default Comp
