import React from "react";
import axios from "axios";
import "./Content.css";

class Content extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      codeValue: "",
      fileName: "",
      scriptString: "",
      selectionStart: "",
      selectionEnd: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
  }

  handleChange(event) {
  //  this.setState({ codeValue: event.target.value });
 //   this.setState({ fileName: event.target.value });
 this.setState({[event.target.codeValue]: event.target.value});
 this.setState({[event.target.fileName]: event.target.value});
  }

  handleSubmit(event) {
    //   alert("Code sent: " + this.state.codeValue);
    event.preventDefault();
    
    const codeBody = this.state.codeValue;
    console.log(codeBody);
    const codeSent = { codeBody };
    axios
      .post("http://localhost:8081/code", codeSent)    
      .catch((err) => {
        console.error(err);
      });
  //    console.log("frontend " + JSON.stringify(codeSent));
      console.log("handle "+ this.state.fileName);
   
  }

  handleKeyDown(event) {
    if (event.keyCode === 9) {
      // tab was pressed
      event.preventDefault();
      var val = this.state.scriptString,
        start = event.target.selectionStart,
        end = event.target.selectionEnd;

      this.setState(
        {
          scriptString: val.substring(0, start) + "\t" + val.substring(end),
        },
        () => {
          this.input.selectionStart = this.input.selectionEnd = start + 1;
        }
      );
    }
  }

  render() {
    //   console.log(this.state.codeValue);
    return (
      <form onSubmit={this.handleSubmit}>
        <label>Or start coding here:</label>
        <br />
        <textarea
          id="id-textarea"
          name = "codeValue"
          className="code-textarea"
   //       value={this.state.codeValue}
          onChange={this.handleChange}
          onKeyDown={this.handleKeyDown}
        />

        <br /><br />
        <label>Denumire fisier:</label>
        <input
          type="text"  
          name="fileName"
   //       value = {this.state.fileName}
          placeholder = "Numele fisierului"
     
        />
        <br />
        <input
          type="submit"
          style={{ backgroundColor: "rgb(138, 15, 15)" }}
          value="Send code"
        />
      </form>
    );
  }
}

export default Content;
