import React from "react";
import useRef from "react"
import "./Content.css";

class Content extends React.Component {
  constructor(props) {
    super(props); 
    this.state = {
      codeValue: "//write your code here",
      scriptString: "",
      selectionStart: "",
      selectionEnd: "",
    };
    
    const textareaRef = useRef();
    const cursorPosition = 0;
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({codeValue: event.target.codeValue });
    this.setState({scriptString:event.target.value})
  }

  handleSubmit(event) {
    alert("Code sent: " + this.state.codeValue);
    event.preventDefault();
  }

  handleKeyDown(event) {
    if (event.keyCode === 9) {
      // tab was pressed
      event.preventDefault();
      var val = this.state.scriptString,
        start = event.target.selectionStart,
        end = event.target.selectionEnd;
        
      this.setState(
        {
          scriptString: val.substring(0, start) + "\t" + val.substring(end),
        },
        () => {
          this.input.selectionStart = this.input.selectionEnd = start + 1;
        }
      );
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Code:
          <br />
          <textarea ref={textareaRef}
          onBlur={() => textareaRef.current.setSelectionRange(cursorPosition, cursorPosition)}
          
            id="id-textarea"
            className="code-textarea"
            value={this.state.codeValue}
         //   onChange={this.handleChange}
         //   onKeyDown={this.handleKeyDown.bind(this)}
          />
        </label>
        <br />
        <input
          type="submit"
          style={{ backgroundColor: "rgb(138, 15, 15)" }}
          value="Send code"
        />
      </form>
    );
  }
}

export default Content;
