import React from "react";

import "./Content.css";

class Content extends React.Component {
  constructor(props) {
    super(props); 
    this.state = {
      codeValue: "//write your code here",
      scriptString: "",
      selectionStart: "",
      selectionEnd: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
  }

  handleChange(event) { 
    this.setState({codeValue: event.target.value });

  }

  handleSubmit(event) {
    alert("Code sent: " + this.state.codeValue);
    event.preventDefault();
  }

  handleKeyDown(event) {
    if (event.keyCode === 9) {
      // tab was pressed
      event.preventDefault();
      var val = this.state.scriptString,
        start = event.target.selectionStart,
        end = event.target.selectionEnd;
        
      this.setState(
        {
          scriptString: val.substring(0, start) + "\t" + val.substring(end),
        },
        () => {
          this.input.selectionStart = this.input.selectionEnd = start + 1;
        }
      );
    }
  }
  sendCode = async (codeBody) =>{

      const codeSent = {
        body: codeBody
      };
      const response = await fetch('http://localhost:8081/',{
        method: 'POST',
        body: JSON.stringify(codeSent),
        headers: {
          'Content-Type' : 'application/json'
        }

      });
      const responseData = await response.json();
    }
  

  render() {
    console.log(this.state.codeValue);
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Or start coding here:
          </label>
          <br />
          <textarea
            id="id-textarea"
            className="code-textarea"
            value={this.state.codeValue}
            onChange={this.handleChange}
            onKeyDown={this.handleKeyDown}
          />
        
        <br />
        
        <input
          type="submit"
          style={{ backgroundColor: "rgb(138, 15, 15)" }}
          value="Send code"
        />
      </form>
  
    );
  }
}

export default Content;
