import React from "react";
import "./ControlPins.css";
import axios from "axios";
class ReadValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      readValue: {
        PD4: "",
        PD5: "",
        PD6: "",
        PA1: "",
        PA2: "",
        PA3: "",
        PD3: "",
