        PC4: "",
        PC3: "",
        PB4: "",
        PB5: "",
      },
    };
    this.handleReadSubmit = this.handleReadSubmit.bind(this);
  }
  componentWillUnmount() {
    clearInterval(this.intervalID);
  }
  handleReadSubmit() {
    axios.get("http://192.168.0.111:8082/get-values").then((res) => {
      console.log(res.data);
