import React, { Component } from "react";
import {
  MDBNavbar,
  MDBNavbarNav,
  MDBNavItem,
  MDBNavLink,
  MDBNavbarToggler,
  MDBCollapse,
  MDBFormInline,
} from "mdbreact";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
const clockStyle = {
  fontSize: "20",
  color: "rgb(138, 15, 15)",
  fontFamily: "cursive",
  fontWeight: "bold"
}
class NavBar extends Component {
  state = {
    isOpen: false,
    date: new Date(),
    
  };


  toggleCollapse = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };
  tick() {
    this.setState({
      date: new Date()
    });
  }

  componentDidMount(){
    this.timerID = setInterval(
      () => this.tick(),1000);
  }

  componentWillUnmount(){
    clearInterval(this.timerID);
  }
  render() {
    return (
      <Router>
        <Switch></Switch>
        <MDBNavbar color="indigo" dark expand="md">
         
  
          <MDBCollapse id="navbarCollapse3" isOpen={this.state.isOpen} navbar>
            <MDBNavbarNav left>
              <MDBNavItem active>
                <MDBNavLink to="/">Home</MDBNavLink>
              </MDBNavItem>
              <MDBNavItem>
                <MDBNavLink to="/stimulus">Stimulus</MDBNavLink>
              </MDBNavItem>
            
            </MDBNavbarNav>
            <MDBNavbarNav right>
              <MDBNavItem>
                <MDBFormInline waves>
                  <div className="md-form my-0">
                    <p style={clockStyle}>
                       {this.state.date.toLocaleTimeString()}
                      <br />
                    </p>
                  </div>
                </MDBFormInline>
              </MDBNavItem>             
            </MDBNavbarNav>
          </MDBCollapse>
        </MDBNavbar>
      </Router>
    );
  }
}

export default NavBar;
