import React from "react";
import "./Home.css";
import Form from "./Form";
import LoadFile from "./LoadFile";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
    };
    this.handleLoadingState = this.handleLoadingState.bind(this);
  }

  handleLoadingStateComp(loading) {
    this.setState({ isLoading: loading });
  }

  render() {
    if (this.state.isLoading === false) {
      return <div>Compiling code...</div>;
    } else {
      return (
        <div>
          <LoadFile />
          <Form handleLoadingState={this.handleLoadingStateComp} />
        </div>
      );
    }
  }
}

export default Home;
