import React from "react";
import photo from "./stm8blue-pinout.png";
import "./ControlPins.css";
class ControlPins extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      optionValuePD3: "",
      optionValuePD2: "",
      optionValuePD1: "",
      optionValuePC7: "",
      optionValuePC6: "",
      optionValuePC5: "",
      optionValuePC4: "",
      optionValuePC3: "",
