import React from "react";
import "./Content.css";
function enableTab(id) {
  var el = document.getElementById(id);
  el.onkeydown = function(e) {
      if (e.keyCode === 9) { // tab was pressed

          // get caret position/selection
          var val = this.value,
              start = this.selectionStart,
              end = this.selectionEnd;

          // set textarea value to: text before caret + tab + text after caret
          this.value = val.substring(0, start) + '\t' + val.substring(end);

          // put caret at right position again
          this.selectionStart = this.selectionEnd = start + 1;

          // prevent the focus lose
          return false;

      }
  };
}

// Enable the tab character onkeypress (onkeydown) inside textarea...
// ... for a textarea that has an `id="my-textarea"`

class Content extends React.Component {
  constructor(props) {
    super(props); 
    this.state = {
      codeValue: "//write your code here",
      scriptString: "",
      selectionStart: "",
      selectionEnd: "",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({codeValue: event.target.codeValue });
  }

  handleSubmit(event) {
    alert("Code sent: " + this.state.codeValue);
    event.preventDefault();
  }

  handleKeyDown(event) {
    if (event.keyCode === 9) {
      // tab was pressed
      event.preventDefault();
      var val = this.state.scriptString,
        start = event.target.selectionStart,
        end = event.target.selectionEnd;
        
      this.setState(
        {
          scriptString: val.substring(0, start) + "\t" + val.substring(end),
        },
        () => {
          this.input.selectionStart = this.input.selectionEnd = start + 1;
        }
      );
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Code:
          <br />
          <textarea
            className="code-textarea" id="id-textarea"
            value={this.state.codeValue}
            onChange={this.handleChange}
            onKeyDown={this.handleKeyDown.bind(this)}
          />
        </label>
        <br />
        <input
          type="submit"
          style={{ backgroundColor: "rgb(138, 15, 15)" }}
          value="Send code"
        />
      </form>
    );
  }
}
enableTab('id-textarea');
export default Content;
