import React from "react";
import "./ControlPins.css";
import axios from "axios";
class ReadValue extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      readValue: {
        PD4: "",
        PD5: "",
        PD6: "",
        PA1: "",
        PA2: "",
        PA3: "",
        PD3: "",
        PD2: "",
        PD1: "",
        PC7: "",
        PC6: "",
        PC5: "",
        PC4: "",
        PC3: "",
        PB4: "",
        PB5: "",
      },
    };
    this.handleReadSubmit = this.handleReadSubmit.bind(this);
  }
  componentWillUnmount() {
    clearInterval(this.intervalID);
  }
  handleReadSubmit() {
    axios.get("http://192.168.0.111:8082/get-values").then((res) => {
      console.log(res);
      //    const pinValues = (JSON.stringify(res.data.data));
    });
  }

  async componentDidMount() {
    console.log(this.state.readValue.PD4);
    try {
      this.intervalID = setInterval(async () => {
        const res = await fetch("http://192.168.0.111:8082/get-values");
        const pinValues = await res.json();
        //     console.log(pinValues);

        this.setState(
          {
            readValue: pinValues,
          },
          () => {
            console.log("In ReadValue ");
            console.log(this.state.readValue);
          }
        );
      }, 3000);
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <div>
        <label className="right-text-read">Read</label>
        <ul className="left-read-value-list">
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PD5}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="dropdownpd6-write-class"
              value={this.state.readValue.PD6}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PA1}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="dropdownpa2-write-class"
              value={this.state.readValue.PA2}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PA3}
            />
          </li>
        </ul>
        <label className="left-text-read">Read</label>
        <ul className="right-read-value-list">
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PD3}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PD2}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValue.PD1}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
          <li>
            <input
              readOnly
              size="1"
              type="text"
              className="read-box"
              value={this.state.readValuePD4}
            />
          </li>
        </ul>
        <button type="submit" onSubmit={this.handleReadSubmit}>
          Read Values
        </button>
      </div>
    );
  }
}
export default ReadValue;
