import React from "react"
import "./Content.css"
class Content extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        value: '//write your code here'
      };
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      event.target.selectionStart = 0;
      event.target.selectionEnd = 0;
      this.setState({value: event.target.value});
      
    }
  
    handleSubmit(event) {
      alert('Code sent: ' + this.state.value);
      event.preventDefault();
    }
    add(event)
    {
      console.log("aici"+event.keyCode);
    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Code:
            <br/>
            <textarea className="code-textarea" value={this.state.value} onChange={this.handleChange} />
          </label>
          <br/>
          <input type="submit" style={{backgroundColor: "magenta"}} value="Send code" />
        </form>
      );
    }
  }


export default Content
